library(tidyverse)

input &lt;- readLines(con = "../../inputs/2015/01.txt")

moves &lt;- tibble(move     = str_split_1(string = input, pattern = ""),
                number   = case_match(move, "(" ~  1, ")" ~ -1),
                position = cumsum(number)) |&gt; 
    rowid_to_column("time")

moves |&gt; 
    summarize(position = sum(number)) |&gt; 
    pull(position) |&gt; 
    cat()
## 232

Part Two

moves |&gt; 
    filter(position == -1) |&gt; 
    slice(1) |&gt; 
    pull(time) |&gt; 
    cat()
