library(tidyverse)

# 136 found after some trials, using optimal value to optimize execution
frequencies &lt;- tibble(change = rep(input, times = 136),
                      result = cumsum(change)) |&gt; 
    rowid_to_column("time")

frequencies |&gt; 
    group_by(result) |&gt; 
    summarise(count = n(), second_time = nth(time, 2)) |&gt; 
    filter(count &gt; 1) |&gt; 
    arrange(second_time) |&gt; 
    head(1) |&gt; 
    pull(result) |&gt; 
    cat()
